import Message from './../Message/Message'
import { CSSTransition, TransitionGroup } from 'react-transition-group'  ; 
import './style.css';

const Messages = ({ messages }) => {

  const Ref = useRef(null);
  const [wolcome, setWolcome] = useState('');

  // const scrollToBottom = () => {
