import React, { useEffect } from 'react'
import './style.css';

const Message = ({ message}) => {


  console.log(message.fonction) ;

  const down = async () =>
            {
              console.log('clicked');
            const response = await fetch('http://localhost:5000/download-pdf');
            console.log(response);
            const blob = await response.blob();
            const url = URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = 'releve_notes.pdf';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            URL.revokeObjectURL(url); 
            
               }
               
  const handleClick=()=>{
    alert('clicked') ; 
  }
 
  return (
    <>
      {message.isBot ? (
        <div className=" row bot">
          <div className='col-1 px-1'>
          <i class="fa-solid fa-robot"></i>
          </div>
          <div className=" col bot_msg">
            <span className="">
              {message.text}
              <p dangerouslySetInnerHTML={{ __html: message.codeHtml }}></p>
            </span>
          </div>
        </div>
      ) : (
        <div className="row user ">
          <div className="col user_msg">
             <span className="">{message.text}</span>
          </div>
          <div className="col-1">
          <i className="fas fa-user" />
          </div>
        </div>
      )}
    </>
  )
}

export default Message
