import React, { useState } from 'react';
import { ReactMic } from "react-mic";


const uploadAudioFile = async (audioData) => {
    const formData = new FormData();
    formData.append("audio", audioData.blob, "recording.wav");
    const response = await axios.post("/api/transcribe", formData);
    return response.data;
};

const Record = () => {
    const [isRecording, setIsRecording] = useState(false);
    const [audioData, setAudioData] = useState(null);

    
    const startRecording = () => {
        setIsRecording(true);
    };
    
    const stopRecording = () => {
        setIsRecording(false);
    };
    
    const onData = (recordedData) => {
        setAudioData(recordedData);
    };
    
    const onStop = (recordedData) => {
        setAudioData(recordedData);
        // Envoyer le fichier audio au serveur pour la transcription ici
        uploadAudioFile(recordedData) ; 
    };

    


  return (
    <div>
      <button onClick={startRecording}>Démarrer l'enregistrement</button>
      <button onClick={stopRecording}>Arrêter l'enregistrement</button>
      <ReactMic
        record={isRecording}
        onData={onData}
        onStop={onStop}
        mimeType="audio/wav"
        strokeColor="#000000"
        backgroundColor="#FF4081"
      />
    </div>
  )
}

export default Record
