import  { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import Messages from './../Messages/Messages' ; 
import './styles.css';

const Chat = () => {
  const [responses, setResponses] = useState([])
  const [currentMessage, setCurrentMessage] = useState('');
  const containerRef = useRef(null);
  const [userProfile, setUserProfile] = useState(''); 

  const userAccount=(nom)=>{
      setUserProfile(nom) ; 
  }

  // const welcoming = ()=>{
  //   const welocomeMessage = {
  //     text : 'entrer vote code apoge' , 
  //     isBot : true  ,  
  //   }

  
  
  useEffect(() => {
    // welcoming();
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  },);

  const languageCode = 'fr'
  // const queryText = currentMessage;
  const sessionId = Math.random()*10;


  const handleMessageSubmit = (queryText) => {
    // const data = {languageCode ,queryText ,  sessionId }
    const sendData = () => {
        axios.post("http://localhost:5000/api/chatbot", {languageCode , queryText , sessionId })
           .then((res) =>{
            console.log(res.data);
            if(res.data.nom){
              setUserProfile(res.data.nom) ;
            }
              // i created this object to store all data comes from http://localhost:5000/api/chatbot
            const responseData ={
              text: res.data.response,
              isBot: true,
              codeHtml : res.data.html, 
              nom : res.data.nom
            }
              if (res.status===200){
                setResponses((responses) => [...responses, responseData]) ; 
              }
           })
           .catch(err => console.log(err))
        }
        
          sendData();
  }

  const handleMessageChange = (event) => {
    setCurrentMessage(event.target.value)
  }

  const handleSubmit = (event) => {
    const message = {
      text: currentMessage,
      isBot: false,
      
    }
    if (event.key === 'Enter' && event.target.value !== '') {
      setResponses((responses) => [...responses, message])
      handleMessageSubmit(message.text);
      setCurrentMessage('');
    }
  }


const welcoming = (
    <div className='welcoming'>
        <p>bonjour, je suis votre assitant scolaire, voici les services que je peut en repondre :</p>
        <ul>
          <li>consultation des notes</li>
          <li>Chercher de fomations</li>
          <li>demander un rendez-vous </li>
          <li>demander releve des notes </li>
        </ul>
        <p className='text-black fw-'>S'il vous plais saisir votre code apoge avant toute autre chose</p>
    </div>
)

  return (
    
    <div className="container rounded d-flex  container_wrapper">

      <div className="row  border-bottom">
        <div className="col">
          <img className='py-2' src="http://www.fstg-marrakech.ac.ma/FST/images/LOGO_FST.png" alt="" />
          <h2 className=' text-dark text-center py-2'>ChatBot</h2>
        </div>
        <div className="col">
          {
               (userProfile) ?<i class="fa-solid fa-user"> {userProfile} </i>: ''
          }
          
        </div>
          <p className=' ' >
            {  
              (!responses[0]) ? welcoming : ''
            }
          </p>
      </div>

      <div ref={containerRef} className="row chat_wrapper">
        <div className="col">
          <Messages userAccount={userAccount} messages={responses}/>
        </div>
      </div>
      <div className="row input_wrapper">
        <div className="col d-flex input_col">
          <input
          value={currentMessage}
          onChange={handleMessageChange}
          onKeyDown={handleSubmit}
          type="text"
          className='form-control'
          placeholder='enter you msg here' />
          <button  className="btn btn-primary btn-sm " >send </button>
        </div>
      </div>
    </div>
  )
}


export default Chat ;
