import { useEffect , useState} from 'react';

import axios from 'axios';


function App() {

  const [backendData, setBackendData] = useState([{}]) ; 
  const [userInput, setUserInput] = useState() ; 


  // const dataToSend = { "student": ["student1", "student2", "student3"]};
  const dataToSend = "";

  const reactData = [{ id: 1, name:' Tom'}, { id: 2, name:' Sarah'}];

  let sendData = () => {
  axios.post("http://localhost:5000/api/posting", {dataToSend,reactData })
     .then(res => console.log('Data send'))
     .catch(err => console.log(err))
  }


  useEffect(() => {

    fetch('/api')
    .then(response => response.json())
    .then(data => {
      setBackendData(data) ; 
      console.log(data)
    })
    .catch(error => console.error(error));


    // fetch('/api/posting?value=hello',{
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json'
    //   },
    //   body:JSON.stringify(dataToSend)
    // })
  },[])


  const handleChange= (e)=>{
    setUserInput(e.target.value) ; 
  } ; 

  const handleClick =()=>{
    sendData()
  }


  return (
    <div className="App">
      <form >
        <input placeholder='your message...' type="text" value={userInput} onChange={handleChange}/>
        <button onClick={handleClick}>send</button>
      </form>
    </div>
  );
}

export default App;
